targets.expr:
(
  count (
    # not (ok:1 and empty:2)
    arubacx_fans_status{site=~"$site", group=~"$group"} == 0
  ) by (host, group, site, application, segment, environment,instance) 
)
or 0 * (
  count ( 
    arubacx_fans_status{site=~"$site", group=~"$group"} == 1
  ) by (host, group, site, application, segment, environment,instance)
)

(
  count (
    arubacx_ps_status{site=~"$site", group=~"$group"} == 0
  ) by (host, group, site, application, segment, environment,instance)
)
or
(
  0 *   count ( 
    arubacx_ps_status{site=~"$site", group=~"$group"} == 1
  ) by (host, group, site, application, segment, environment,instance)
)

sum (
    arubacx_system_cpu_usage_percent{site=~"$site", group=~"$group"}
  ) by (host, group, site, application, segment, environment,instance)

sum (
    arubacx_system_memory_usage_percent {site=~"$site", group=~"$group"}
  ) by (host, group, site, application, segment, environment,instance)

avg (
    rate(arubacx_if_in_bytes{site=~"$site", group=~"$group"}[5m]) * 8
  ) by (host, group, site, application, segment, environment,instance)

avg (
    rate(arubacx_if_out_bytes{site=~"$site", group=~"$group"}[5m]) * 8
  ) by (host, group, site, application, segment, environment,instance)
